!function(){class t{constructor(){this.restUrl=wpextendedNotices.restUrl,this.restNonce=wpextendedNotices.restNonce,this.init()}init(){document.querySelectorAll(".notice-dismiss").forEach((t=>{t.addEventListener("click",(t=>{const e=t.target.closest("[data-notice-id]");e&&this.dismissNotice(e.dataset.noticeId)}))}))}getFetchOptions(t="DELETE"){return{method:t,credentials:"same-origin",headers:{"Content-Type":"application/json","X-WP-Nonce":this.restNonce}}}async dismissNotice(t){try{const e=await fetch(`${this.restUrl}?notice_id=${encodeURIComponent(t)}`,this.getFetchOptions("DELETE"));if(!e.ok)throw Error("HTTP error! status: "+e.status);if((await e.json()).dismissed){const e=document.querySelector(`[data-notice-id="${t}"]`);e&&e.remove()}}catch(t){console.error("Error dismissing notice:",t)}}}document.addEventListener("DOMContentLoaded",(()=>{new t}))}();